mapper.select:
SELECT bat_user_id, bat_coin
FROM batting

    game_id = #{gameId}

SELECT (SELECT user_nickname FROM user WHERE user_id = bat_user_id) as batterNickname, bat_coin
FROM batting
WHERE game_id = #{gameId} AND bat_player_id = #{playerId}
ORDER BY bat_date ASC LIMIT 1;
